import React,{Component} from 'react'
import axios from 'axios'
class Tasks extends Component
{

  state={
      tasks:[]
  }

    componentDidMount(){
        axios.get('http://localhost:300/todos/get/'+sessionStorage.getItem('email'))
         .then(response => {console.log(response);this.setState({tasks:response.data})})
         .catch(error => {console.log(error)})
    }
    deleteTodo(e){
        const id=e.target.id;
        axios.delete('http://localhost:300/todos/'+id)
         .then(response => {console.log(response);this.props.history.push('/user')})
         .catch(error => {console.log(error)})
    }
    addTask(){
        console.log('add')
    }
render(){
    console.log(this.state)
    const tasks=this.state.tasks.length?(this.state.tasks.map( task => { return (<div><i class="fa fa-plus-circle float-right fa-4x add" aria-hidden="true"><a onClick={this.addTask}></a></i><div className="container bcontent">
    <hr />
    <div className="list-group card">

        <div className="row no-gutters">

            <div className="col-sm-12">
            <h2 className="card-header">{task.task}</h2>
                <div className="card-body">
                  <div><a onClick={this.deleteTodo}><i id={task._id}  class="fa fa-trash-o float-right"></i></a>></div>
                  <div><b className="card-text">Added Date : {task.added_date}</b></div> 
                    <div><b className="card-text">Last Date: {task.last_date}</b></div>
                </div>
            </div>
        </div>
    </div>
</div></div>)})):(
    <div className="text-center jumbotron">Loading post...</div>
);  
return(
  <div className="container">

                {tasks}
                </div>
)
}
}
export default Tasks
