import React,{Component} from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css';
class Add extends Component{
    constructor() {
        super();
        // this.handleSubmit = this.handleSubmit.bind(this);

      }

      state={
          added_date: new Date(),
          task: null,
          last_date: null
      }


      onChangeDate=(date)=>{
        this.setState({
            last_date:date
        })
    }

render(){
    return(
<div className='container center_div'>

<form onSubmit={this.handleSubmit} className="align-middle spacing">
<h1 className="sign_in_text text_center"> Add Task</h1>

<br>
</br>
<div className='container form-group'>
<label className='float-left'>Enter Task: </label> 
<input onChange={ this.handleChange } type="text"  name='task' id="inputTask" className="form-control btn-lg" placeholder="Enter Task" required autofocus=""/>
</div>
<div className='container form-group'>
<label className='float-left'>Enter Last Date: </label> 
<input onChange={ this.handleChange } type="date"  name='task' id="inputTask" className="form-control btn-lg" placeholder="Enter Task" required autofocus=""/>
</div>
<div>
</div>

<div className='container form-group'>
<button className="btn btn-primary btn-block btn-lg"><i className="fas fa-sign-in-alt"></i> Sign Up</button>
</div>
<hr/>        
</form>
</div>
)
}
}
export default Add;
